import React from "react";
import { withRouter } from "react-router-dom";
import Slider from "react-slick";
import { createMuiTheme } from "@material-ui/core/styles";

import "./preview-collections.scss";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";

import CollectionItem from "../collection-item/Collection-item";

// material-ui-----------style config-------
const theme = createMuiTheme({
    overrides: {
        MuiIconButton: {
            root: {
                "&:hover": {
                    backgroundColor: "black"
                }
            }
        }
    }
});
// ----------------------------------

const Test = ({ title, items, routeName, match, history }) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        prevArrow: <ArrowBackIosIcon color="primary" className="arrow-hover" />,
        nextArrow: (
            <ArrowForwardIosIcon color="primary" className="arrow-hover" />
        )
    };
    return (
        <div className="collection-preview">
            <h1
                className="title"
                onClick={() => history.push(`${match.path}/${routeName}`)}
            >
                {title}
            </h1>

            <Slider {...settings}>
                {items.map(item => (
                    <CollectionItem key={item.id} item={item} />
                ))}
            </Slider>
        </div>
    );
};

export default withRouter(Test);
